commits.content:
:root {
  --main-color: #060B35;
  --secondary-color: #616070;
  --blue:#00ACDC;
  --yellow: #FFDE6A;
}

body {
  margin: 0;
  padding: 0;
  box-sizing: border-box;
  font-family: Roboto, Arial, sans-serif;
}

.card {
  display: flex;
  flex-direction: column;
  align-items: center;
  width: 200px;
  flex: 1;
  margin: 16px;
}

.card__review {
  display: flex;
}

.card__image {
  width: 100%;
  margin-bottom: 40px;
}
.card__item {
  font-size: 12px;
  text-align: left;
}
.card__item-name {
  color: var(--secondary-color);
  font-weight: 500;
  font-size: 12px;
}
.card__item-product-code {
  font-size: 10px;
  align-self: flex-start;
  color: var(--secondary-color);
}
.card__price {
  display: flex;
  align-items: center;
  justify-content: space-between;
  width: 100%;
  gap: 80px;
}
.card__price-text {
  font-size: 12px;
  color: var(--secondary-color);
}
.card__price-amount {
  color: var(--main-color);
  font-size: 17px;
}
.card__button {
  height: 40px;
  text-transform: uppercase;
  border-radius: 5px;
  width: 100%;
  background-color: var(--blue);
  color: white;
  font-weight: 700;
  font-size: 14px;
  line-height: 16px;
  border: 1px solid var(--blue);
}
.card__button:hover {
  background-color: white;
  color: var(--blue);
  border: 1px solid var(--blue);
  cursor: pointer;
}

.review__stars {
  display: flex;
  align-items: center;
}
.review__amount {
  font-size: 10px;
}/*# sourceMappingURL=index.css.map */
